styles.css:
#container_m {
    z-index: 1000;
    height: 100vh;
    width: 100%;
    position: fixed;
    top: 0;
    pointer-events: none;
    user-select: none;
  }
  .mouse_tag h2, .cursor_new h2 {
    display: none;
  }
  .cursor {
    width: var(--size);
    height: var(--size);
    border-radius: 50%;
    position: absolute;
    left: 0;
    top: 0;
    transform: translate(-50%, -50%);
    pointer-events: none;
    z-index: 1000;
  }
  .cursor--large {
    --size: 30px;
    border: 2px solid #f1f1f1;
  }
  .cursor--small {
    --size: 8px;
    background: #ffffff;
  }
  .wp-block-navigation__responsive-container-open svg {
    width: 60px;
    height: 60px;
  }
  .faixa_bg {

    background-repeat: no-repeat;
    background-position: left;
    background-position-y: bottom;
    background-size: cover;
  }
  .menu_desk_r {
    background-color: #000;
    background: linear-gradient(0deg, #000 0%, #0d0d0d 100%);
    float: right;
    margin-top: -100vh;
  }
  .menu_desk_r .icones {
    writing-mode: vertical-lr;
    height: 100vh;
  }
  .menu_desk_r .icones h2 {
    text-align: right;
    /* color: transparent !important; */
    text-shadow: none;
    /* -webkit-text-stroke: 2px #ffffff80; */
    font-size: 5rem !important;
    font-weight: 400;
    padding: 42px 22px;
  }
  .stroke_text {
    color: #ffffff00;
    -webkit-text-stroke: 2px #fff;
  }
  .stroke_text2 {
    color: #ffffff00;
    -webkit-text-stroke: 1px #ffffff80;
    letter-spacing: 4px;
  }
  .video_group {
    position: relative;
  }
  .video_text {
    z-index: 1;
    position: relative;
    margin: 0;
  }
  .video_section video {
    width: 100%;
    height: 100%;
    position: absolute;
    object-fit: cover;
    z-index: 0;
  }
  .h1_hero span {
    color: var(--wp--preset--color--second) !important;
  }
  .marquee {
    overflow: hidden;
    width: 100%;
  }
  .marquee__inner {
    display: flex;
    position: relative;
    white-space: nowrap;
    gap: 50px;
  }
  .marquee__item {
    position: relative;
    display: flex;
    align-items: center;
    padding: 42px;
  }
  .pt-30 {
    padding-top: 30vh !important;
  }
  .pt-20 {
    padding-top: 20vh !important;
  }
  .pr-20 {
    padding-right: 2rem !important;
  }
  .pb-30 {
    padding-bottom: 30vh !important;
  }
  .botao_video {
    position: relative;
    z-index: 1;
  }
  .section_x {
    height: 100vh;
    width: 100%;
  }
  .section_x2 {
    height: 100vh;
    width: 100%;
  }
  .outer {
    overflow-x: hidden;
  }
  .mask {
    overflow-y: hidden;
  }
  .slider {
    width: 280%;
    height: 100vh;
    display: flex;
    flex-wrap: nowrap;
  }
  .slider2 {
    width: 200%;
    height: 100vh;
    display: flex;
    flex-wrap: nowrap;
  }
  .accordion-content {
    height: 0;
    overflow: hidden;
  }
  .accordion-content.expanded {
    height: 0;
    overflow: hidden;
  }
  .wp-block-latest-posts__post-excerpt {
    color: var(--wp--preset--color--second) !important;
    margin-bottom: 1em;
    margin-top: 0.5em;
    font-weight: 300;
    font-size: 1.5rem;
  }
  .wp-block-latest-posts__post-author,
  .wp-block-latest-posts__post-date {
    font-size: 1.2rem;
    margin-top: 8px;
  }
  .wp-block-latest-posts__featured-image img {
    max-width: 100% !important;
  }
  .client_item {
    padding-top: 48px;
    padding-bottom: 48px;
  }
  .gap-o {
    gap: 0 !important;
    margin-block-start: 0 !important;
  }
  .b_top {
    border-top: 1px solid #fff;
  }
  .b_bottom {
    border-bottom: 1px solid #fff;
  }
  .b_left {
    border-left: 1px solid #fff;
  }
  .b_right {
    border-right: 1px solid #fff;
  }
  .social_midia_btn {
    color: #000 !important;
    background-color: #fff;
    padding: 6px 26px;
    border-radius: 25px;
    display: table;
  }

  @media (max-width: 768px) {
    h1,
    h2,
    h3 {
      font-size: 2.8rem !important;
    }
    .b_top {
      border-top: none !important;
    }
    .b_bottom {
      border-bottom: none !important;
    }
    .b_left {
      border-left: none !important;
    }
    .b_right {
      border-right: none !important;
    }
    .menu_desk_r {
      display: none !important;
    }

    .faixa_bg .wp-block-group .accordion-menu .accordion-group h2 {
      font-size: 1.8rem !important;
    }
    .faixa_bg
      .wp-block-group
      .accordion-group
      .accordion-menu
      .wp-block-image
      img {
      width: 40px !important;
    }
    .accordion-group,
    .wp-block-group-is-layout-constrained {
      max-width: 100% !important;
    }
    .wp-block-group-is-layout-constrained p,
    .wp-block-group-is-layout-constrained h2 {
      max-width: 90% !important;
    }
    .cursor--large {
      display: none !important;
    }
    .cursor--small {
      display: none !important;
    }
    .menu_desk {
      display: none !important;
    }
    .arrow {
      display: none !important;
    }
    .mobile_menu {
      display: none !important;
    }
  }


  .menu_desk {
    width: 12vw;
    background-color: #000;
    background: linear-gradient(180deg, #000 0%, #0d0d0d 100%);
    position: absolute;
    left: 0;
    top: 0;
    height: 90vh;
  }
  .vertical-text {
    font-size: 1.2rem;
    font-weight: 400;
    color: var(--wp--preset--color--white);
    font-family: var(--wp--preset--font-family--urbanist) !important;
    z-index: 100000;
  }
  .menu_desk .icones {
    writing-mode: vertical-lr;
    position: fixed;
    display: flex;
    padding: 192px 25px 0 25px;
  }
  .menu_desk .icones p svg {
    margin-left: -10px;
    margin-bottom: 15px;
  }

  .menu_desk_r {
    background-color: #000;
    background: linear-gradient(0deg, #000 0%, #0d0d0d 100%);
    height: 100vh;
    float: right;
    margin-top: -200px;
  }

  .menu_desk_r .icones {
    writing-mode: vertical-lr;
    height: 100vh;
  }
  .menu_desk_r .icones span {
    color: transparent;
    text-shadow: none;
    -webkit-text-stroke: 1px #ffffff54;
    font-family: var(--fonte_principal);
    font-size: 7rem;
    font-style: normal;
    font-weight: 400;
  }
  .color_menu_desk a {
    color: var(--wp--preset--color--second) !important;
padding-left: 12px
} 
.color_menu_desk svg {
color: var(--wp--preset--color--second);
}
@media (min-width: 600px) and (max-width: 900px) {
  .wp-block-latest-posts.columns-3 li {
      width: 45%;
      margin: 12px
    }
}

.marquee__item {
display: table; }
